package javaprograms;

public class Cat {
	String name;
	int age;
public Cat()
{
	name="unknown";
	age=0;
}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Cat myObj=new Cat();
		System.out.println(myObj.name);
		System.out.println(myObj.age);

	}

}
