package javaprograms;

public class basicfor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=7,i;
		for(i=1;i<=n;i++)
		{
			if(i%2==0)
			{
			System.out.println("The even number is:"+i);
			}
			else 
				System.out.println("The odd number is:"+i);
		}

	}

}
